import React from 'react';

const Navbar = () => {
  const navbarStyle = {
    position: 'fixed',
    top: 0, // Ensure it's at the top
    left: 0,
    width: '100%',
    zIndex: 1000,
    backgroundColor: 'black',
    color: '#fff',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
  };

  const linkStyle = {
    color: '#fff',
    textDecoration: 'none',
    margin: '0 15px',
    fontSize: '16px',
    transition: 'color 0.3s ease',
  };

  const handleSignOut = () => {
    localStorage.removeItem('isAuthenticated');
    window.location.href = '/'; // Redirect to login
  };

  return (
    <div style={navbarStyle} className="navbar">
      <div className="logo">
        <a href="/main" style={linkStyle}>Lost & Found</a>
      </div>
      <div className="links">
        <a href="/main" style={linkStyle}>Home</a>
        <a href="#about-us" style={linkStyle}>About Us</a>
        <a href="#contact-us" style={linkStyle}>Contact Us</a>
        <a href="#profile" style={linkStyle}>Profile</a>
        <a href="/" onClick={handleSignOut} style={linkStyle}>Sign Out</a>
      </div>
    </div>
  );
};

export default Navbar;
