import React from 'react';
import '../styles/style.css';
import "../styles/card.css";
import Navbar from '../components/navbar';
import RecentFound from '../components/RecentFound';

const LostFoundForm = () => {
  const centerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
    marginBottom: '40px',
    paddingLeft: '40px',
  };

  const leftCardStyle = {
    borderRadius: "10px", // Rounded corners
    //padding: "40px", // Space inside the card
    paddingLeft: '50px', // Increased padding on the left for horizontal space
    paddingRight: '50px', // Increased padding on the right for horizontal space
    display: "flex", // Flexbox to center content
    flexDirection: "column", // Stack content vertically
    justifyContent: "center", // Center content vertically
    alignItems: "center", // Center content horizontally
    height: "100%", // Full height of the column
    backgroundColor: 'black',
    width: '100%', // Allows it to expand horizontally
    maxWidth: '800px', // Maximum width for the card
  };


  const rowfirst = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
    marginTop: '20px',
    width: '100%',
  };

  const columnfirst = {
    flex: '1 1 50%', // 50% width for each column
    padding: '20px',
  };

  const formCard = {
    backgroundColor: '#f4f4f4',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    textAlign: 'left',
    maxWidth: '100%',
  };

  const formTitle = {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    marginBottom: '10px',
  };

  const formGroup = {
    marginBottom: '20px',
  };

  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '8px',
    display: 'block',
  };

  const inputStyle = {
    width: '100%', // Increased width to take up more horizontal space
    padding: '5px 70px', // Increased horizontal padding (left and right), decreased vertical padding (top and bottom)
    borderRadius: '8px',
    border: '1px solid #ccc',
    transition: 'border-color 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
    marginBottom: '10px',
    fontSize: '1.1rem', // Slightly larger text
  };

  const buttonStyle = {
    padding: '14px 26px',
    borderRadius: '8px',
    border: 'none',
    backgroundColor: '#007bff',
    color: '#fff',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease-in-out, transform 0.2s ease-in-out',
  };

  const imageStyle1 = {
    width: '400%',
    maxWidth: '400px',
    borderRadius: '8px',
  };

  return (
    <div>
      <Navbar/>
      <div style={centerStyle}>
        <div>
          <h2>Report a Lost Item</h2>
        </div>
      </div>

      <div style={rowfirst}>
        {/* Left Column with Card */}
        <div style={columnfirst}>
          <div className="one col-lg-6 col-md-3 col-12 w-50 m-2 align-items-center lost-found-card" style={leftCardStyle}>
            <h5 style={formTitle}>Lost Item Report</h5>
            <form id="lostItemForm">
              <div style={formGroup}>
                <label style={labelStyle} htmlFor="location">Where It Was Lost:</label>
                <input
                  style={inputStyle}
                  type="text"
                  id="location"
                  name="location"
                  required
                  placeholder="Enter the location"
                />
              </div>

              <div style={formGroup}>
                <label style={labelStyle} htmlFor="lostItemName">Lost Item:</label>
                <input
                  style={inputStyle}
                  type="text"
                  id="lostItemName"
                  name="lostItemName"
                  required
                  placeholder="Enter the lost item"
                />
              </div>

              <div style={formGroup}>
                <label style={labelStyle} htmlFor="description">Description:</label>
                <textarea
                  style={inputStyle}
                  id="description"
                  name="description"
                  required
                  placeholder="Describe the item"
                ></textarea>
              </div>

              <div style={formGroup}>
                <label style={labelStyle} htmlFor="dateLost">Date When Lost:</label>
                <input
                  style={inputStyle}
                  type="date"
                  id="dateLost"
                  name="dateLost"
                  required
                />
              </div>

              <button type="submit" style={buttonStyle}>
                Submit
              </button>
            </form>
          </div>
        </div>

        {/* Right Column with Image */}
        <div style={columnfirst}>
          <img
            src="/images/lost.png"  
            alt="Lost and Found"
            style={imageStyle1}
          />
        </div>
      </div>
    </div>
  );
};

export default LostFoundForm;
