import React, { useState } from 'react';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import './style.css';  // Assuming you kept the original CSS file

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false);

  return (
    <div className="container">
      <input type="checkbox" id="flip" />
      <div className="cover">
        <div className="front">
          <img src="images/lost-and-found.jpg" alt="Cover Front" />
        </div>
        <div className="back">
          <img className="backImg" src="images/lost-and-found.jpg" alt="Cover Back" />
        </div>
      </div>
      <div className="forms">
        <div className="form-content">
          {isSignup ? <SignupForm /> : <LoginForm />}
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
