import React, { useState, useEffect } from "react";

const RecentLost = () => {
  const [lostItems, setLostItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const sectionStyle = {
    paddingTop: "40px",
    paddingBottom: "40px",
    backgroundColor: "#f8f9fa",
  };

  const headingStyle = {
    textAlign: "center",
    marginBottom: "24px",
    paddingTop: "40px",
    fontSize: "2rem",
    fontWeight: "bold",
  };

  const hrStyle = {
    borderTop: "2px solid #6c757d",
    width: "80%",
    margin: "auto",
  };

  const containerStyle = {
    maxWidth: "1200px",
    margin: "auto",
  };

  const cardGroupStyle = {
    display: "flex",
    justifyContent: "space-between",
    gap: "16px",
    flexWrap: "wrap",
  };

  const cardStyle = {
    flex: "1 1 calc(33.333% - 16px)",
    maxWidth: "calc(33.333% - 16px)",
    backgroundColor: "#fff",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    borderRadius: "8px",
    overflow: "hidden",
  };

  const cardImgStyle = {
    width: "100%",
    height: "200px",
    objectFit: "cover",
  };

  const cardBodyStyle = {
    padding: "16px",
  };

  const cardTitleStyle = {
    fontSize: "1.25rem",
    fontWeight: "bold",
  };

  const cardFooterStyle = {
    padding: "8px 16px",
    backgroundColor: "#f1f1f1",
    fontSize: "0.9rem",
    color: "#6c757d",
  };

  const bottomStyle = {
    textAlign: "center",
    marginTop: "40px",
    paddingBottom: "40px",
  };

  const learnMoreStyle = {
    display: "inline-block",
    padding: "10px 20px",
    fontSize: "1rem",
    fontWeight: "bold",
    color: "#fff",
    backgroundColor: "#007bff",
    textDecoration: "none",
    borderRadius: "4px",
    transition: "background-color 0.3s",
  };

  useEffect(() => {
const fetchData = async () => {
  try {
    const response = await fetch("http://127.0.0.1:8000/recent-lost-items/");
    if (!response.ok) {
      throw new Error("Failed to fetch data");
    }
    const data = await response.json();
    console.log(data); // Check the API response here
    setLostItems(data);
  } catch (err) {
    setError(err.message);
  } finally {
    setLoading(false);
  }
};


    fetchData();
  }, []);

  if (loading) {
    return <p style={{ textAlign: "center" }}>Loading...</p>;
  }

  if (error) {
    return <p style={{ textAlign: "center", color: "red" }}>{error}</p>;
  }

  return (
    <section style={sectionStyle}>
      <div style={headingStyle}>
        RECENT LOST ITEMS
        <div style={{ display: "flex", justifyContent: "center" }}>
          <hr style={hrStyle} />
        </div>
      </div>
      <div style={containerStyle}>
        <div style={cardGroupStyle}>
          {lostItems.map((item) => (
            <div style={cardStyle} key={item.id}>
              <img
                src={item.imageUrl || "images/placeholder.jpg"} // Fallback if image URL is missing
                alt={item.name}
                style={cardImgStyle}
              />
              <div style={cardBodyStyle}>
                <h5 style={cardTitleStyle}>{item.name}</h5>
                <p>
                  Reported By: <strong>{item.reportedBy}</strong>
                </p>
              </div>
              <div style={cardFooterStyle}>
                <small>Last updated {item.updatedAt}</small>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div style={bottomStyle}>
        <a href="https://aitlostandfound.netlify.app/" style={learnMoreStyle}>
          View More
        </a>
      </div>
    </section>
  );
};

export default RecentLost;
