import React from 'react';
import { Link } from 'react-router-dom';
import LostItemForm from '../pages/LostItemForm';
import Found from '../pages/Found';

const Navbar = ({ onProfileClick }) => {

  const handleSignOut = () => {
    localStorage.removeItem('isAuthenticated');
    window.location.href = '/'; // Redirect to login
  };

  return (
    <div style={navbarStyle} className="navbar">
      <div className="logo">
        <Link to="/main" style={linkStyle}>Lost & Found</Link>
      </div>
      <div className="links">
        <Link to="/main" style={linkStyle}>Home</Link>
        <Link to="#about-us" style={linkStyle}>About Us</Link>
        <Link to="lost" style={linkStyle}>Contact Us</Link>
        {/* Lost option link */}
        <Link to="./LostItemForm" style={linkStyle}>Lost</Link> {/* Added the Lost Item link */}
        <Link to="./Found" style={linkStyle}>Found</Link>
        <button onClick={onProfileClick} style={{ cursor: 'pointer' }}>
          Profile
        </button>
      </div>
    </div>
  );
};

const navbarStyle = {
  position: 'fixed',
  top: 0, // Ensure it's at the top
  left: 0,
  width: '100%',
  zIndex: 1000,
  backgroundColor: 'black',
  color: '#fff',
  padding: '10px 20px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
};

const linkStyle = {
  color: '#fff',
  textDecoration: 'none',
  margin: '0 15px',
  fontSize: '16px',
  transition: 'color 0.3s ease',
};

export default Navbar;
