import React, { useState,useEffect } from 'react';

const UserAccount = ({ onSignOut }) => {
  const [user, setUser] = useState({
    username: '',
      email: '',
      mobile: '',
    profilePicture: '', // Placeholder profile picture
  });

  useEffect(() => {
    // Fetch user details from the backend
    fetch('/user-details/', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Use your token mechanism
      },
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          console.error(data.error);
        } else {
          setUser({
            username: data.name,
            email: data.email,
            mobile: data.mobile_number,
            profilePicture: data.profile_picture || 'https://via.placeholder.com/150',
          });
        }
      })
      .catch((error) => console.error('Error fetching user details:', error));
  }, []);





  const handleUserChange = (field, value) => {
    setUser({
      ...user,
      [field]: value,
    });
  };



  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setUser((prevUser) => ({
          ...prevUser,
          profilePicture: reader.result,
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveChanges = () => {
    alert('Account details updated successfully!');
    // Make API call to save changes
  };

  return (
    <div className="user-account" style={{ marginTop: '70px', padding: '20px' }}>
      <h2>User Account</h2>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {/* Profile Picture */}
        <div>
          <img
            src={user.profilePicture}
            alt="Profile"
            style={{ borderRadius: '50%', width: '150px', height: '150px', objectFit: 'cover' }}
          />
        </div>

        {/* Username */}
        <div style={{ marginTop: '20px' }}>
          <label>Username: </label>
          <input type="text" value={user.username} readOnly style={{ backgroundColor: '#f5f5f5', border: 'none' }} />
        </div>

        {/* Email */}
        <div style={{ marginTop: '10px' }}>
          <label>Email: </label>
          <input type="email" value={user.email} readOnly style={{ backgroundColor: '#f5f5f5', border: 'none' }} />
        </div>

        {/* Mobile Number */}
        <div style={{ marginTop: '10px' }}>
          <label>Mobile: </label>
          <input type="text" value={user.mobile} readOnly style={{ backgroundColor: '#f5f5f5', border: 'none' }} />
        </div>
      </div>
    </div>
  );
};

export default UserAccount;
