import React, { useState } from 'react';
import './style.css'; // Ensure your CSS is imported
import ll from './images/ll.jpg';

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false); // Manage whether to show login or signup

  const submitLoginForm = () => {
    const username = document.getElementById('login-username').value;
    const password = document.getElementById('login-password').value;

    const data = {
      username,
      password,
    };

    // Perform your login logic here
    fetch('http://127.0.0.1:8000/auth/login/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'Login successful') {
          window.location.href = 'index.html'; // Redirect after successful login
        } else {
          alert('Login failed: ' + (data.error || 'Unknown error'));
        }
      })
      .catch((error) => {
        alert('Error: ' + error);
      });
  };

  const submitSignupForm = () => {
    const username = document.getElementById('signup-username').value;
    const phone = document.getElementById('signup-phone').value;
    const email = document.getElementById('signup-email').value;
    const password1 = document.getElementById('signup-password1').value;
    const password2 = document.getElementById('signup-password2').value;

    if (password1 !== password2) {
      alert("Passwords don't match!");
      return;
    }

    const data = {
      username,
      email,
      password1,
      password2,
    };

    // Perform your signup logic here
    fetch('http://127.0.0.1:8000/auth/signup/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'User created successfully!') {
          window.location.href = 'index.html'; // Redirect after successful signup
        } else {
          alert('Signup failed: ' + data.errors);
        }
      })
      .catch((error) => {
        alert('Error: ' + error);
      });
  };

  return (<div>
    <div className="heading">Army Institute of Technology</div>
    <div> <img src="images/ll.jpg" alt="Cover Image" /></div>
    <div className="container">

      <input type="checkbox" id="flip" />
      <div className="cover">
        <div className="front">
          <img src="images/lost-and-found.jpg" alt="Cover Image" />
        </div>
        <div className="back">
          <img className="backImg" src="images/lost-and-found.png" alt="Cover Image" />
        </div>
      </div>
      <div className="forms">
        <div className="form-content">
          {/* Login Form */}
          {!isSignup && (
            <div className="login-form">
              <div className="title">Login</div>
              <form id="login-form">
                <div className="input-boxes">
                  <div className="input-box">
                    <i className="fas fa-envelope"></i>
                    <input
                      type="text"
                      id="login-username"
                      placeholder="Enter your username"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="login-password"
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                  <div className="text">
                    <a href="#">Forgot password?</a>
                  </div>
                  <div className="button input-box">
                    <input type="button" value="Login" onClick={submitLoginForm} />
                  </div>
                  <div className="text sign-up-text">
                    Don't have an account?{' '}
                    <label htmlFor="flip" onClick={() => setIsSignup(true)}>
                      Sign up now
                    </label>
                  </div>
                </div>
              </form>
            </div>
          )}

          {/* Signup Form */}
          {isSignup && (
            <div className="signup-form">
              <div className="title">Signup</div>
              <form id="signup-form">
                <div className="input-boxes">
                  <div className="row">
                    <div className="input-box">
                      <i className="fas fa-user"></i>
                      <input
                        type="text"
                        id="signup-username"
                        placeholder="Enter your username"
                        required
                      />
                    </div>
                    {/* <div className="input-box">
                      <i className="fas fa-id-card"></i>
                      <input
                        type="text"
                        id="signup-phone"
                        placeholder="Enter your phone number"
                        required
                      />
                    </div> */}
                  </div>
                  {/* <div className="input-box">
                    <i className="fas fa-envelope"></i>
                    <input
                      type="text"
                      id="signup-email"
                      placeholder="Enter your email"
                      required
                    />
                  </div> */}
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="signup-password1"
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="signup-password2"
                      placeholder="Confirm your password"
                      required
                    />
                  </div>
                  <div className="button input-box">
                    <input type="button" value="Signup" onClick={submitSignupForm} />
                  </div>
                  <div className="text sign-up-text">
                    Already have an account?{' '}
                    <label htmlFor="flip" onClick={() => setIsSignup(false)}>
                      Login now
                    </label>
                  </div>
                </div>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
    </div>
  );
};

export default LoginSignup;
