import React from 'react';
import '../styles/style.css';
const Home = () => {
  return (
    <div className='body'>
      <div className='rowfirst' style={rowfirst}>
      <section class="discovery container section-1">
      <div className='columnfirst' style={columnfirst}>
        <div
          class="
            text
            col-lg-6 col-md-6 col-12
            w-50
            d-flex
            flex-column
            align-items-center
            justify-content-center
          "
        >
          <h1 class="got-lost-heading mb" style={topp}>GOT LOST</h1>

          <p>
            Something lost it's way and came to you just to show the world how
            honest you are!
          </p>
          <p class="mb-5">
            Or if you've lost something just chill! People here are damnnn cool!
          </p>
          <div class="shrink">
          </div>
        </div>
        </div>
        <div className='columnfirst' style={columnfirst}>

        <div class="img col-lg-6 col-md-6 col-12">
          <img
            src="images/lost-and-found.png"
            alt="image"
            class="img-fluid lost-found"
          />
        </div> 
        </div>
      </section>
      </div>















        <text className=''>hello</text>
      {/* Inline CSS */}
      {/* <style>
        {`
