import React, { useState } from 'react';
import '../styles/Profile.css'; // Include a CSS file for styling

const Profile = ({ onSignOut, userData }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState(userData.name);
  const [profilePicture, setProfilePicture] = useState(userData.profilePicture);

  const handleEditToggle = () => {
    setIsEditing((prev) => !prev);
  };

  const handleSave = () => {
    // Save changes logic here
    setIsEditing(false);
    // Example: Save to localStorage or send to an API
    const updatedData = { ...userData, name, profilePicture };
    localStorage.setItem('userData', JSON.stringify(updatedData));
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfilePicture(reader.result); // Set image as base64 string
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-picture">
          <img src={profilePicture} alt="Profile" />
          {isEditing && (
            <input
              type="file"
              accept="image/*"
              onChange={handleProfilePictureChange}
            />
          )}
        </div>
        <div className="profile-details">
          <p>
            <strong>Registration Number:</strong> {userData.registrationNumber}
          </p>
          <p>
            <strong>Registered Email:</strong> {userData.email}
          </p>
          {isEditing ? (
            <div>
              <label htmlFor="name">Name:</label>
              <input
                id="name"
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
          ) : (
            <p>
              <strong>Name:</strong> {name}
            </p>
          )}
        </div>
        <div className="profile-actions">
          {isEditing ? (
            <button onClick={handleSave}>Save</button>
          ) : (
            <button onClick={handleEditToggle}>Edit</button>
          )}
          <button onClick={onSignOut}>Logout</button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
