import React from 'react';

const LostFoundSection = () => {
  const centerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
    marginBottom: '40px',
  };

  const rowfirst = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
    marginTop: '20px',
  };

  const columnfirst = {
    flex: '1',
    padding: '10px',
  };

  const card = {
    backgroundColor: '#f4f4f4',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    textAlign: 'left',
  };

  const cardtitle = {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    marginBottom: '10px',
  };

  const textLeft = {
    marginBottom: '10px',
    fontSize: '1rem',
    color: '#333',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px 20px',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    display: 'inline-block',
    marginTop: '15px',
  };

  const imageStyle1 = {
    width: '100%',
    maxWidth: '400px',
    borderRadius: '8px',
  };

  return (
    <div>
      <div style={centerStyle}>
        <div>
          <h2>Our Services</h2>
        </div>
      </div>

      <div style={rowfirst}>
        {/* Left Column with Card */}
        <div style={columnfirst}>
          <div
            className="one col-lg-6 col-md-3 col-12 w-50 m-2 align-items-center lost-found-card"
            style={card}
          >
            <h5 className="card-title" style={cardtitle}>
              Lost
            </h5>
            <ul className="my-3">
              <li style={textLeft}>
                Only the property lost on College or Hostel can be reported.
              </li>
              <li style={textLeft}>
                Maximum time for filing any lost report should be 2 days.
              </li>
              <li style={textLeft}>
                Try to be as much detailed as you can be.
              </li>
              <li style={textLeft}>
                Head towards the form below if you have lost your property.
              </li>
            </ul>
            <div className="shrink">
              <div className="button-container">
                <a
                  className="learn-more px-5"
                  href="forms/lost.html"
                  style={buttonStyle}
                >
                  Next
                </a>
              </div>
            </div>
          </div>
        </div>

        {/* Right Column with Image */}
        <div style={columnfirst}>
          <img
            src="images/lost-card.png"
            alt="Lost and Found"
            style={imageStyle1}
          />
        </div>
      </div>
    </div>
  );
};

export default LostFoundSection;
