import React, { useState } from "react";
import "../styles/style.css";

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false);

  return (
    <div style={rowfirst}>
      {/* Left Column with Image */}
      <div style={columnfirst}>
        <img
          src="images/community.png"
          alt="Lost and Found"
          style={imageStyle}
        />
      </div>

      {/* Right Column with Form */}
      <div style={columnfirst}>
        <div style={leftCardStyle}>
          {!isSignup ? (
            <div>
              <h1 className="got-lost-heading">
                <span style={gotlost}>Login</span>
              </h1>
              <form id="login-form">
                <div className="input-box">
                  <input type="text" id="login-username" placeholder="Enter your email" required />
                </div>
                <div className="input-box">
                  <input type="password" id="login-password" placeholder="Enter your password" required />
                </div>
                <div className="button input-box">
                  <input type="button" value="Login" />
                </div>
                <div className="text sign-up-text">
                  Don't have an account? <label onClick={() => setIsSignup(true)}>Sign up now</label>
                </div>
              </form>
            </div>
          ) : (
            <div>
              <h1 className="got-lost-heading">
                <span style={gotlost}>Sign Up</span>
              </h1>
              <form id="signup-form">
                <div className="input-box">
                  <input type="text" id="signup-username" placeholder="Enter your name" required />
                </div>
                <div className="input-box">
                  <input type="email" id="signup-email" placeholder="Enter your email" required />
                </div>
                <div className="input-box">
                  <input type="password" id="signup-password1" placeholder="Enter your password" required />
                </div>
                <div className="button input-box">
                  <input type="button" value="Signup" />
                </div>
                <div className="text sign-up-text">
                  Already have an account? <label onClick={() => setIsSignup(false)}>Login now</label>
                </div>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;

const rowfirst = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  gap: "20px",
  height: "100vh",
};

const columnfirst = {
  width: "50%",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const imageStyle = {
  width: "80%",
  borderRadius: "10px",
};

const leftCardStyle = {
  background: "#fff",
  padding: "20px",
  borderRadius: "8px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  width: "80%",
};

const gotlost = {
  color: "#007bff",
  fontWeight: "bold",
};
