 <div className="rowfirst" style={rowfirst}>
      <section className="discovery container section-1" style={{ flex: "1" }}>
        <div className="columnfirst" style={columnfirst}>
          <div
            className="text col-lg-6 col-md-6 col-12 w-50 d-flex flex-column align-items-center justify-content-center"
          >
            <h1 className="got-lost-heading mb" style={topp}>
              GOT LOST
            </h1>

            <p>
              Something lost it's way and came to you just to show the world how
              honest you are!
            </p>
            <p className="mb-5">
              Or if you've lost something just chill! People here are damnnn
              cool!
            </p>
            <div className="shrink"></div>
          </div>
        </div>
        <div className="columnfirst" style={columnfirst}>
          <div className="img col-lg-6 col-md-6 col-12">
            <img
              src="images/lost-and-found.png"
              alt="image"
              className="img-fluid lost-found"
            />
          </div>
        </div>
      </section>
    </div>















