import React, { useState } from "react";
impo

const LostItemForm = () => {
  const [formData, setFormData] = useState({
    personName: "",
    gmailId: "",
    contactNo: "",
    location: "",
    lostItemName: "",
    description: "",
    dateLost: "",
    photo: null,
  });
  const [confirmation, setConfirmation] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData((prevData) => ({
      ...prevData,
      photo: file,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setConfirmation("Your lost item has been reported successfully!");
    // Here you can add code to submit the form data, e.g., to an API
  };

  const sectionStyle = {
    display: "flex",
    maxWidth: "1000px",
    margin: "0 auto",
    padding: "20px",
    backgroundColor: "#f4f4f4",
  };

  const formContainerStyle = {
    flex: "1",
    padding: "20px",
    borderRadius: "8px",
    maxHeight: "80vh",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const imageContainerStyle = {
    flex: "1",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  const formGroupStyle = {
    marginBottom: "25px",
  };

  const labelStyle = {
    display: "block",
    marginBottom: "8px",
    fontWeight: "bold",
  };

  const inputTextareaStyle = {
    width: "calc(100% - 12px)",
    padding: "12px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    transition: "border-color 0.3s ease-in-out, box-shadow 0.3s ease-in-out",
  };

  const inputFocusStyle = {
    outline: "none",
    borderColor: "#007bff",
    boxShadow: "0 0 8px rgba(0, 123, 255, 0.4)",
  };

  const inputHoverStyle = {
    borderColor: "#999",
  };

  const buttonStyle = {
    padding: "14px 26px",
    borderRadius: "8px",
    border: "none",
    backgroundColor: "#007bff",
    color: "#fff",
    cursor: "pointer",
    transition: "background-color 0.3s ease-in-out, transform 0.2s ease-in-out",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const buttonHoverStyle = {
    backgroundColor: "#0056b3",
    transform: "translateY(-2px)",
  };

  const confirmationStyle = {
    marginTop: "20px",
    textAlign: "center",
    fontWeight: "bold",
    color: "#007bff",
  };

  return (
    <section style={sectionStyle}>
      <div style={formContainerStyle}>
        <h1>Report a Lost Item</h1>
        <form onSubmit={handleSubmit}>
          <div style={formGroupStyle}>
            <label htmlFor="personName" style={labelStyle}>
              Your Name:
            </label>
            <input
              type="text"
              id="personName"
              name="personName"
              value={formData.personName}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="gmailId" style={labelStyle}>
              Gmail ID:
            </label>
            <input
              type="email"
              id="gmailId"
              name="gmailId"
              value={formData.gmailId}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="contactNo" style={labelStyle}>
              Contact Number:
            </label>
            <input
              type="tel"
              id="contactNo"
              name="contactNo"
              value={formData.contactNo}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="location" style={labelStyle}>
              Where It Was Lost:
            </label>
            <input
              type="text"
              id="location"
              name="location"
              value={formData.location}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="lostItemName" style={labelStyle}>
              Lost Item:
            </label>
            <input
              type="text"
              id="lostItemName"
              name="lostItemName"
              value={formData.lostItemName}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="description" style={labelStyle}>
              Description:
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="dateLost" style={labelStyle}>
              Date When Lost:
            </label>
            <input
              type="date"
              id="dateLost"
              name="dateLost"
              value={formData.dateLost}
              onChange={handleChange}
              style={inputTextareaStyle}
              required
            />
          </div>

          <div style={formGroupStyle}>
            <label htmlFor="photo" style={labelStyle}>
              Upload Photo of the Lost Item (Optional):
            </label>
            <input
              type="file"
              id="photo"
              name="photo"
              accept="image/*"
              onChange={handleFileChange}
              style={inputTextareaStyle}
            />
          </div>

          <button type="submit" style={buttonStyle}>
            Submit
          </button>
        </form>

        <div id="confirmation" style={confirmationStyle}>
          {confirmation}
        </div>
      </div>

      <div style={imageContainerStyle}>
        <img src="lost.png" alt="Image" style={{ width: "100%", borderRadius: "8px", height: "auto" }} />
      </div>
    </section>
  );
};

export default LostItemForm;
