import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const MainPage = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    username: 'JohnDoe',
    email: 'johndoe@example.com',
    password: 'password123',
  });

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    window.location.href = '/'; // Redirect to the login page
  };


  const handleProfileChange = (field, value) => {
    setProfile({
      ...profile,
      [field]: value,
    });
  };

  return (
    <div>
      {/* Navbar */}
      <nav>
        <ul>
          <li><a href="/home">Home</a></li>
          <li><a href="/lost-found">Lost & Found</a></li>
          <li><a href="/about-us">About Us</a></li>
          <li><a href="/contact-us">Contact Us</a></li>
          <li><a href="#" onClick={handleLogout}>Sign Out</a></li>
        </ul>
      </nav>

      {/* Profile Section */}
      <div className="profile">
        <h2>Profile</h2>
        <div>
          <label>Username: </label>
          <input
            type="text"
            value={profile.username}
            onChange={(e) => handleProfileChange('username', e.target.value)}
          />
        </div>
        <div>
          <label>Email: </label>
          <input
            type="email"
            value={profile.email}
            onChange={(e) => handleProfileChange('email', e.target.value)}
          />
        </div>
        <div>
          <label>Password: </label>
          <input
            type="password"
            value={profile.password}
            onChange={(e) => handleProfileChange('password', e.target.value)}
          />
        </div>
        <button onClick={() => alert('Profile updated!')}>Save Changes</button>
      </div>

      {/* Main Content */}
      <div className="content">
        <h1>Welcome to our website!</h1>
        <p>Here, you'll find a variety of services including lost & found, user profiles, and much more!</p>
      </div>
    </div>
  );
};

export default MainPage;
