  const [isSignup, setIsSignup] = useState(false);

  const submitLoginForm = () => {
    const email = document.getElementById('login-email').value;
    const password = document.getElementById('login-password').value;

    fetch('http://127.0.0.1:8000/api/login/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'Login successful.') {
          localStorage.setItem('isAuthenticated', 'true');
          alert('Login successful!');
          navigate('/main'); // Redirect to the main page
        } else {
          alert(`Login failed: ${data.error || 'Unknown error'}`);
        }
      })
      .catch((error) => {
        alert(`Error: ${error}`);
      });
  };

  const submitSignupForm = () => {
    const name = document.getElementById('signup-name').value;
    const email = document.getElementById('signup-email').value;
    const phone = document.getElementById('signup-phone').value;
    const password1 = document.getElementById('signup-password1').value;
    const password2 = document.getElementById('signup-password2').value;

    if (password1 !== password2) {
      alert("Passwords don't match!");
      return;
    }

    fetch('http://127.0.0.1:8000/api/signup/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name, email, phone, password: password1 }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'User created successfully!') {
          alert('Signup successful! You can now log in.');
          setIsSignup(false);
        } else {
          alert(`Signup failed: ${data.error || 'Unknown error'}`);
        }
      })
      .catch((error) => {
        alert(`Error: ${error}`);
      });
  };

  return (
    <div>
      <div className="heading">Army Institute of Technology</div>
      <div className="container">
        <div className="forms">
          <div className="form-content">
            {!isSignup && (
              <div className="login-form">
                <div className="title">Login</div>
                <div className="input-boxes">
                  <div className="input-box">
                    <i className="fas fa-envelope"></i>
                    <input
                      type="email"
                      id="login-email"
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="login-password"
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                  <div className="button input-box">
                    <input type="button" value="Login" onClick={submitLoginForm} />
                  </div>
                  <div className="text sign-up-text">
                    Don't have an account?{' '}
                    <label onClick={() => setIsSignup(true)}>Sign up now</label>
                  </div>
                </div>
              </div>
            )}

            {isSignup && (
              <div className="signup-form">
                <div className="title">Signup</div>
                <div className="input-boxes">
                  <div className="input-box">
                    <i className="fas fa-user"></i>
                    <input
                      type="text"
                      id="signup-name"
                      placeholder="Enter your name"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-envelope"></i>
                    <input
                      type="email"
                      id="signup-email"
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-phone"></i>
                    <input
                      type="text"
                      id="signup-phone"
                      placeholder="Enter your phone number"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="signup-password1"
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                  <div className="input-box">
                    <i className="fas fa-lock"></i>
                    <input
                      type="password"
                      id="signup-password2"
                      placeholder="Confirm your password"
                      required
                    />
                  </div>
                  <div className="button input-box">
                    <input type="button" value="Signup" onClick={submitSignupForm} />
                  </div>
                  <div className="text sign-up-text">
                    Already have an account?{' '}
                    <label onClick={() => setIsSignup(false)}>Login now</label>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
